import osimport asyncioimport ssl #SSL bağlantısı içinimport slixmppfrom getpass import getpass# Windows'da asenkron çalışmayı etkinleştirmeif os.name == 'nt':    asyncio.set_event_loop_policy(asyncio.WindowsSelectorEventLoopPolicy())class Bot(slixmpp.ClientXMPP):    def __init__(self, jid, password, message):        slixmpp.ClientXMPP.__init__(self, jid, password)        self.add_event_handler("session_start", self.session_start)        self.add_event_handler("message", self.message)        self.msg = message    def session_start(self, event):        self.send_presence()        self.get_roster()        self.send_message(mto=self.boundjid, mbody=self.msg, mtype='chat')    def message(self, msg):        if msg['type'] in ('chat', 'normal'):            print(f"Alınan mesaj: {msg['body']}")if __name__ == '__main__':    jid = input("JID: ")    password = getpass("Password: ")    message = input("Mesaj: ")    xmpp = Bot(jid, password, message)        # SSL doğrulamasını devre dışı bırak    xmpp.ssl_version = ssl.PROTOCOL_SSLv23    xmpp.ca_certs = None        xmpp.connect()    xmpp.process(forever=True)